    .accordion-control-section,
    #source_link {
        margin: 0;
    }
}

#athletics li,
#racing_games li,
#water_games li,
#indoor_games li {
    line-height: 36px;
    list-style-type: none;
    text-indent: 16px;
}

@-moz-document url-prefix() {
    .e-accordion .e-content table {
      border-collapse: initial;
  }
  }

.e-accordion table {
    border-collapse: collapse;
    border-spacing: 0;
    width: 100%;
}

.e-accordion table th,
.e-accordion table td {
    font-weight: normal;
    padding: 5px;
    text-align: left;
    border: 1px solid #ddd
}

.product_title {
    text-align: center;
    margin: 20px 0;
    padding: 10px 0;
    text-overflow: ellipsis;
    font-weight: bold;
    font-size: 16px;
}

