#dropdownbutton-control {
    width: 100%;
    margin: 11% 0;
    text-align: center;
}

.dropdownbutton-section {
    width: 80%;
    margin: auto;
}

#dropdownbutton-control .col-xs-12 {
    margin: 15px 0;
}

@media only screen and (max-width: 500px) {
    #dropdownbutton-control .col-lg-6 {
        margin-bottom: 30px;
    }
}

@media only screen and (min-width: 1200px) {
    #dropdownbutton-control .col-lg-6 {
        width: 25%;
    }
}

@font-face {
    font-family: 'e-ddb';
    src: url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.e-dropdown-btn .e-ddb-icons,
.e-dropdown-popup .e-ddb-icons {
    font-family: 'e-ddb';
    speak: none;
    font-size: 55px;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.e-logout::before {
    content: "\e702";
}

.e-dashboard::before {
    content: "\e704";
}

.e-profile::before {
    content: "\e703";
}

.e-notifications::before {
    content: "\e700";
}

.e-settings::before {
    content: "\e701";
}

.material .e-logout::before,
.bootstrap .e-logout::before {
    content: "\e706";
}

.material .e-dashboard::before,
.bootstrap .e-dashboard::before {
    content: "\e708";
}

.material .e-profile::before,
.bootstrap .e-profile::before {
    content: "\e709";
}

.material .e-notifications::before,
.bootstrap .e-notifications::before {
    content: "\e707";
}

.material .e-settings::before,
.bootstrap .e-settings::before {
    content: "\e705";
}

