    @keyframes hidden {
        0% {
            transform: translateY(-100px);
            display: block !important;
            z-index: 20;
        }
        10% {
            transform: translateY(-90px);
        }
        20% {
            transform: translateY(-80px);
        }
        30% {
            transform: translateY(-70px);
        }
        40% {
            transform: translateY(-60px);
        }
        50% {
            transform: translateY(-50px);
        }
        60% {
            transform: translateY(-40px);
        }
        70% {
