/**
* ej2 Context Menu styles
*/
@font-face {
    font-family: 'e-context-menu';
    src:
    url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.e-cm-icons {
    font-family: 'e-context-menu';
    font-style: normal;
    font-variant: normal;
    font-weight: normal;
    line-height: 1;
    text-transform: none;
}

#contextmenu-control {
    margin: 5% 25%;
    width: auto;
    -webkit-touch-callout: none; /* iOS Safari */
    -webkit-user-select: none; /* Safari */
}

#contextmenutarget {
    border: 1px dashed;
    height: 250px;
    padding: 10px;
    position: relative;
    text-align: center;
    color: gray;
    line-height: 17;
    font-size: 14px;
}

@media only screen and (max-width: 700px) {
    #contextmenu-control {
        margin: 5% 10%;
        width: auto;
    }
    #contextmenutarget {
        line-height: 19;
        font-size: 12px;
    }
}

