    endDate: "10/20/2018"
  },
  {
    id: "Formalize Specification",
    Category: "Start Project",
    duration: "2",
    startDate: "10/21/2018",
    endDate: "12/22/2018"
  },
  {
    id: "Write Documentation",
    Category: "Start Project",
    duration: "1",
    startDate: "12/23/2018",
    endDate: "01/22/2019"
  },
  {
    id: "Release Prototype",
    Category: "Design",
    duration: "1",
    startDate: "01/23/2019",
    endDate: " 02/23/2019"
  },
  {
    id: "Testing",
    Category: ["Formalize Specification", "Release Prototype"],
    duration: "2",
    startDate: "02/24/2019",
    endDate: "04/22/2019"
  },
  {
    id: "Release Project",
    Category: "Release Prototype",
    duration: "1",
    startDate: "04/23/2019",
    endDate: "05/24/2019"
  },
  {
    id: "Review Changes",
    Category: "Write Documentation",
    duration: "1",
    startDate: "05/25/2019",
    endDate: "06/26/2019"
  },
  {
    id: "Publish Documentation",
    Category: "Review Changes",
    duration: "1",
    startDate: "06/21/2019",
    endDate: "07/22/2019"
  },
  {
    id: "Finish",
    Category: ["Publish Documentation", "Release Project"],
    duration: "1",
    startDate: "07/23/2019",
    endDate: "08/24/2019"
  }
];

export interface DataInfo {
  [key: string]: string;
}

function getTextElement(
  text: string,
  alignment: HorizontalAlignment,
  width?: number,
  valignment?: VerticalAlignment
): DiagramElement {
  let textElement: TextElement = new TextElement();
  textElement.content = text;
  textElement.width = width;
  textElement.height = 25;
  textElement.horizontalAlignment = alignment;
  textElement.verticalAlignment = valignment;
  textElement.style.strokeWidth = 1;
  textElement.style.strokeColor = "#b5b5b5";
  textElement.style.fill = "transparent";
  textElement.style.color = "#3c3c3c";
  textElement.relativeMode = "Object";
  return textElement;
}

let sDate: string = "startDate";
let eDate: string = "endDate";
let duration: string = "duration";

let addRows: Function = (column: StackPanel, node: NodeModel) => {
  column.children.push(
    getTextElement((node.data as DataInfo)[sDate], "Left", 70)
  );
  column.children.push(
    getTextElement((node.data as DataInfo)[duration], "Center", 30)
  );
  column.children.push(
    getTextElement((node.data as DataInfo)[eDate], "Right", 70)
  );
};

let diagramInstance: DiagramComponent;

export class PertChart extends SampleBase<{}, {}> {
  rendereComplete() {
    diagramInstance.fitToPage();
  }
  render() {
    return (
      <div className="control-pane">
        <div className="control-section">
          <div style={{ width: "100%" }}>
            <DiagramComponent
              id="diagram"
              ref={diagram => (diagramInstance = diagram)}
              width={"100%"}
              height={"499px"}
              snapSettings={{ constraints: SnapConstraints.None }}
              dataSourceSettings={{
                id: "id",
                parentId: "Category",
                dataManager: new DataManager(pertChartData as JSON[]), //binds the external data with node
                doBinding: (
                  nodeModel: NodeModel,
                  data: object,
                  diagram: Diagram
                ) => {
                  let shape: string = "shape";
                  let name: string = "id";
                  /* tslint:disable:no-string-literal */
                  nodeModel["shape"] = { type: "Text" };
                }
              }}
              layout={{
                type: "ComplexHierarchicalTree",
                orientation: "LeftToRight",
                verticalSpacing: 100,
                horizontalSpacing: 70
              }} //Sets the default values of connector
              getConnectorDefaults={(
                connector: ConnectorModel,
                diagram: Diagram
              ) => {
                connector.type = "Straight";
                connector.style.strokeColor = "#979797";
                connector.targetDecorator.width = 10;
                connector.targetDecorator.height = 10;
                connector.targetDecorator.style = {
                  fill: "#979797",
                  strokeColor: "#979797"
                };
                return connector;
              }}
              //used to customize template of the node.
              setNodeTemplate={(node: NodeModel) => {
                return getNodeTemplate(node);
              }}
              tool={DiagramTools.ZoomPan}
            >
              <Inject
                services={[
                  DataBinding,
                  HierarchicalTree,
                  ComplexHierarchicalTree
                ]}
              />
            </DiagramComponent>
          </div>
        </div>
      </div>
    );
  }
}

//customization of the node template.
function getNodeTemplate(node: NodeModel): StackPanel {
  let table: StackPanel = new StackPanel();
  table.style.fill = "#0069d9";
  table.orientation = "Vertical";
  let nameKey: string = "id";
  let stack: StackPanel = new StackPanel();
  stack.children = [];
  stack.height = 25;
  stack.orientation = "Horizontal";
  stack.style.fill = "white";
  stack.horizontalAlignment = "Stretch";
  addRows(stack, node);
  table.children = [
    getTextElement((node.data as DataInfo)[nameKey], "Stretch", 170, "Stretch"),
    stack
  ];
  (table.children[0].style as TextStyleModel).color = "white";
  (table.children[0].style as TextStyleModel).fontSize = 14;
  return table;
}

ReactDOM.render(<PertChart />, document.getElementById('sample'));

