 /* Layout customization */

 .layoutWrapper {
    line-height: initial;
    border: 1px solid black;
    width: 285px;
    height: 505px;
    margin: auto;
    margin-bottom: 15px;
    border-radius: 28px;
    position: relative;
    visibility: hidden;
}

.layoutWrapper .speaker {
    border: 1px solid black;
    border-radius: 5px;
    width: 33.33333333%;
    height: 5px;
    margin: 15px auto 0px auto;
    position: relative;
}

.layoutWrapper .outerButton {
    width: 30px;
    height: 30px;
    border: 1px solid black;
    border-radius: 50%;
    position: absolute;
    bottom: calc(0% + 10px);
    left: calc(50% - 15px);
}

.layoutWrapper .camera {
    position: absolute;
    left: calc(-15% - 10px);
    top: -100%;
    width: 10px;
    height: 10px;
    border-radius: 50%;
    border: 1px solid black;
}

.layoutWrapper .layout {
    border: 1px solid black;
    margin: 20px 13px 0px 13px;
}

.layout #list-container .e-list-item {
    cursor: pointer;
}

.layoutWrapper.e-device-layout {
    visibility: visible;
}

.e-visbile-layer {
    visibility: visible;
}

/* ListView customization */

@font-face {
    font-family: 'Phone Icons';
    src: url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

@font-face {
    font-family: 'Call-icons';
    src: url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.layout #list-container #all.e-listview.e-list-template .e-list-wrapper.e-list-multi-line,
.layout #list-container #received.e-listview.e-list-template .e-list-wrapper.e-list-multi-line,
.layout #list-container #missed.e-listview.e-list-template .e-list-wrapper.e-list-multi-line {
    padding: 5px;
}

.layout #list-container #all.e-listview.e-list-template .e-list-wrapper.e-list-avatar,
.layout #list-container #received.e-listview.e-list-template .e-list-wrapper.e-list-avatar,
.layout #list-container #missed.e-listview.e-list-template .e-list-wrapper.e-list-avatar {
    padding-left: 50px;
    padding-right: 5px;
}

.layout #list-container #all.e-listview.e-list-template .e-list-wrapper.e-list-multi-line.e-list-avatar .e-avatar,
.layout #list-container #received.e-listview.e-list-template .e-list-wrapper.e-list-multi-line.e-list-avatar .e-avatar,
.layout #list-container #missed.e-listview.e-list-template .e-list-wrapper.e-list-multi-line.e-list-avatar .e-avatar {
    top: 5px;
    left: 5px;
}

.layout #list-container #all,
.layout #list-container #received,
.layout #list-container #missed {
    max-height: 360px;
}

.layout #list-container {
    height: 405px;
}

.layout #list-container .received {
    font-family: Call-icons;
}

.layout #list-container .received:before {
    content: '\e700';
    font-size: 8px;
    margin-right: 6px;
}

.layout #list-container .missed {
    font-family: Call-icons;
}

.layout #list-container .missed:before {
    content: '\e702';
    font-size: 8px;
    margin-right: 6px;
}

.layout #list-container .e-icon {
    font-family: 'e-icons';
    background: transparent;
    color: black;
}

.layout #list-container .e-icon:before {
    content: '\e902';
}

/* ListView theme customization */

.highcontrast .layoutWrapper .layout,
.highcontrast .layoutWrapper .camera,
.highcontrast .layoutWrapper .outerButton,
.highcontrast .layoutWrapper .speaker,
.highcontrast .layoutWrapper {
    border-color: white;
}

